import { useState } from "react";
import useInput from "../hook/use-input";

const SimpleInput = () => {
  const {
    value: enteredText,
    hasError: nameInputHasError,
    isValid: enteredNameIsValid,
    valueChangeHandler: nameInputChangeHandler,
    inputBlurHandler: nameInputBlurHandler,
    reset,
  } = useInput((value) => value.trim() !== "");

  // const [enteredText, setEnteredText] = useState("");
  // const [enteredNameTouched, setEnteredNameTouched] = useState(false);

  const [enteredEmailText, setEnteredEmailText] = useState("");
  const [enteredEmailTouched, setEnteredEmailTouched] = useState(false);

  // const [enteredNameIsValid, setEnteredNameIsValid] = useState(false);
  // const enteredNameIsValid = enteredText.trim() !== '';
  // const inputNameBox = !enteredNameIsValid && enteredNameTouched;

  const enteredEmailIsValid = enteredEmailText.includes() !== "@";
  const inputEmailBox = !enteredEmailIsValid && enteredEmailTouched;

  // const [ formIsValid, setFormIsValid ] = useState(false);
  let formIsValid = false;

  // useEffect(() => {
  //   if(enteredNameIsValid){
  //     setFormIsValid(true);
  //   } else {
  //     setFormIsValid(false);
  //   }
  // }, [enteredNameIsValid]);
  if (enteredNameIsValid && enteredEmailIsValid) {
    formIsValid = true;
  } 

  // const nameInputChangeHandler = (event) => {
  //   setEnteredText(event.target.value);
  // };
  const emailInputChangeHandler = (event) => {
    setEnteredEmailText(event.target.value);
  };

  // const nameInputBlurHandler = () => {
  //   setEnteredNameTouched(true);
  // };
  const emailInputBlurHandler = () => {
    setEnteredEmailTouched(true);
  };

  const formSubmissionHandler = () => {
    // event.perventDefault();
    // setEnteredNameTouched(true);
    setEnteredEmailTouched(true);

    if (!enteredNameIsValid && !enteredEmailIsValid) {
      return;
    }

    // setEnteredNameIsValid(true);

    console.log(enteredText);
    console.log(enteredEmailText);

    reset();

    // setEnteredText("");
    // setEnteredNameTouched(false);

    setEnteredEmailText("");
    setEnteredEmailTouched(false);
  };

  const inputNameBoxEffect = nameInputHasError
    ? "form-control invalid"
    : "form-control";
  const inputEmailBoxEffect = inputEmailBox
    ? "form-control invalid"
    : "form-control";

  return (
    <div>
      <div className={inputNameBoxEffect}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          value={enteredText}
          onBlur={nameInputBlurHandler}
          onChange={nameInputChangeHandler}
        />
        {nameInputHasError && <p className="error-text">name must not empty!!</p>}
      </div>
      <div className={inputEmailBoxEffect}>
        <label htmlFor="email">Email</label>
        <input
          type="text"
          id="email"
          value={enteredEmailText}
          onBlur={emailInputBlurHandler}
          onChange={emailInputChangeHandler}
        />
        {inputEmailBox && <p className="error-text">email must not empty!!</p>}
      </div>
      <div className="form-actions">
        <button disabled={!formIsValid} onClick={formSubmissionHandler}>
          Submit
        </button>
      </div>
    </div>
  );
};

export default SimpleInput;
