import { useEffect, useRef, useState } from "react";

const SimpleInput = () => {
  const nameInputRef = useRef();
  const [enteredText, setEnteredText] = useState("");
  const [enteredNameIsValid, setEnteredNameIsValid] = useState(false);
  const [enteredNameTouched, setEnteredNameTouched] = useState(false);

  useEffect(() => {
    if (enteredNameIsValid) {
      console.log("Name Input is Valid!");
    }
  }, [enteredNameIsValid]);

  const nameInputChangeHandler = (event) => {
    setEnteredText(event.target.value);
  };

  const nameInputBlurHandler = () => {
    setEnteredNameTouched(true);
  }

  const formSubmissionHandler = () => {
    // event.perventDefault();
    setEnteredNameTouched(true);

    if (enteredText.trim() === "") {
      setEnteredNameIsValid(false);
      return;
    }
    setEnteredNameIsValid(true);

    console.log(enteredText);

    const enteredValue = nameInputRef.current.value;
    console.log(enteredValue);

    setEnteredText("");
  };

  const inputNameBox = !enteredNameIsValid && enteredNameTouched;

  const inputBoxEffect = inputNameBox
    ? "form-control invalid"
    : "form-control";

  return (
    <div>
      <div className={inputBoxEffect}>
        <label htmlFor="name">Your Name</label>
        <input
          ref={nameInputRef}
          type="text"
          id="name"
          value={enteredText}
          onBlur={nameInputBlurHandler}
          onChange={nameInputChangeHandler}
        />
        {inputNameBox && (
          <p className="error-text">name must not empty!!</p>
        )}
      </div>
      <div className="form-actions">
        <button onClick={formSubmissionHandler}>Submit</button>
      </div>
    </div>
  );
};

export default SimpleInput;
